    }))
    console.log('Você clicou no botão 1');
  }

  handleClick = (numero) => {
    console.log(this);
    console.log(`Você clicou no botão ${numero}`);
  }

  handleClickThree = () => {
    console.log('Você clicou no botão 3');
  }

  render() {
    return (
      <div>
        <h1>Hello World</h1>
        <button onClick={this.handleClickOne}>Botão 1</button>
        <span>{this.state.numeroDeCliques}</span>
        <button onClick={() => {this.handleClick('2')}}>Botão 2</button>
        <button onClick={this.handleClickThree}>Botão 3</button>
      </div>
    )
  }
}

export default App;
