import React from 'react';
import './App.css';

const handleClickOne = () => {
  console.log('Você clicou no botão 1')
}

const handleClickTwo = () => {
  console.log('Você clicou no botão 2')
}

const handleClickThree = () => {
  console.log('Você clicou no botão 3')
}
class App extends React.Component {
  render() {
    return (
      <div>
        <h1>Hello World</h1>
        <button onClick={handleClickOne}>Botão 1</button>
        <button onClick={handleClickTwo}>Botão 1</button>
        <button onClick={handleClickThree}>Botão 1</button>
      </div>
    )
  }
}

export default App;
