    }))
    console.log(`Você clicou no botão ${numero}`);
  }

  render() {
    return (
      <div>
        <h1>Hello World</h1>
        <button onClick={() => {this.handleClick('1')}}>Botão 1</button>
        <span>{this.state.numeroDeCliques}</span>
        <button onClick={() => {this.handleClick('2')}}>Botão 2</button>
        <button onClick={() => {this.handleClick('3')}}>Botão 3</button>
      </div>
    )
  }
}

export default App;
